import React from 'react';
import { TodoList } from './todo';

export const App = (props: {}) => {
    return (
        <TodoList />
    );
};

import React, { useState } from 'react';

const styles = {
    container: {
        padding: 4,
        backgroundColor: `#000000`,
        color: `#FFFFFF`,
    },
    itemContainer: {
        margin: 4,
        padding: 4,
        backgroundColor: `#333333`,
        color: `#FFFFFF`,
    },
    row: {
        display: `flex`,
        flex: 1,
        flexDirection: `row`,
        alignItems: `center`,
    },
    flex1: {
        display: `flex`,
        flex: 1,
    },
    button: {
        margin: 4,
        padding: 8,
        backgroundColor: `#111111`,
    },
} as const;

const getUniqueKey = () => {
    return `${Date.now()}:${Math.random()}`;
};
const createNewTodoItem = () => {
    return { key: getUniqueKey(), text: `New Task`, isDone: false };
};

export const TodoList = (props: {}) => {

    const [items, setItems] = useState([createNewTodoItem()] as TodoItemData[]);
    const changeItem = (value: TodoItemData) => {
        setItems(s => s.map(x => x.key === value.key ? value : x));
    };
    const addItem = () => {
        setItems(s => [...s, createNewTodoItem()]);
    };
    const deleteItem = (value: TodoItemData) => {
        setItems(s => s.filter(x => x.key !== value.key));
    };

    return (
        <div style={styles.container}>
            {items.map(x => (
                <TodoItem key={x.key} item={x} onChange={changeItem} onDelete={deleteItem} />
            ))}
            <div style={styles.row}>
                <div style={styles.button} onClick={addItem} >
                    <span>{`‚ûï Add`}</span>
                </div>
            </div>
        </div>
    );
};

type TodoItemData = {
    key: string;
    text: string;
    isDone: boolean;
};
const TodoItem = (props: { item: TodoItemData, onChange: (value: TodoItemData) => void, onDelete: (value: TodoItemData) => void }) => {

    const [isEditing, setIsEditing] = useState(false);
    const [itemText, setItemText] = useState(props.item.text);
    const changeText = () => {
        setIsEditing(false);
        props.onChange({ ...props.item, text: itemText });
    };
    const toggleDone = () => {
        props.onChange({ ...props.item, isDone: !props.item.isDone });
    };

    return (
        <div style={styles.itemContainer}>
            {!isEditing && (
                <div style={styles.row} onClick={toggleDone}>
                    <div>
                        <span>{props.item.isDone ? `‚úî üê±‚Äçüèç ` : `‚óª üòæ `}</span>
                    </div>
                    <div style={styles.flex1}>
                        <span>{itemText}</span>
                    </div>
                    <div style={styles.button}
                        onClick={(e) => { e.preventDefault(); setIsEditing(true); }}
                    >
                        <span>{`‚úè Edit`}</span>
                    </div>
                    <div style={styles.button}
                        onClick={(e) => { e.preventDefault(); props.onDelete(props.item); }}
                    >
                        <span>{`‚ùå Delete`}</span>
                    </div>
                </div>
            )}
            {isEditing && (
                <div style={styles.row}>
                    <input style={styles.flex1} type='text' value={itemText}
                        autoFocus
                        onChange={e => setItemText(e.target.value)}
                        onBlur={e => { e.preventDefault(); changeText(); }}
                        onKeyDown={e => { if (e.key === `Enter`) { e.preventDefault(); changeText(); } }}
                    />
                    <div style={styles.button} onClick={changeText}>
                        <span>{`‚úî Done`}</span>
                    </div>
                </div>
            )}
        </div>
    );
};

import React from 'react';
import { TodoList } from './todo';

export const App = (props: {}) => {
    return (
        <TodoList />
    );
};

import React, { useEffect, useRef, useState } from 'react';
import { apiService } from './service';
import { TodoItemData } from './types';

const styles = {
    container: {
        padding: 4,
        backgroundColor: `#000000`,
        color: `#FFFFFF`,
    },
    itemContainer: {
        margin: 4,
        padding: 4,
        backgroundColor: `#333333`,
        color: `#FFFFFF`,
    },
    row: {
        display: `flex`,
        flex: 1,
        flexDirection: `row`,
        alignItems: `center`,
    },
    flex1: {
        display: `flex`,
        flex: 1,
    },
    button: {
        margin: 4,
        padding: 8,
        backgroundColor: `#111111`,
    },
} as const;

const getUniqueKey = () => {
    return `${Date.now()}:${Math.random()}`;
};
const createNewTodoItem = () => {
    return { key: getUniqueKey(), text: `New Task`, isDone: false };
};

export const TodoList = (props: {}) => {

    const [items, setItems] = useState([] as TodoItemData[]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null as null | { message: string });

    const isMounted = useRef(true);

    useEffect(() => {
        (async () => await loadData())();

        return () => {
            isMounted.current = false;
        };
    }, []);

    const loadData = async () => {
        setLoading(true);
        try {
            const result = await apiService.loadData();
            if (!isMounted.current) { return; }
            setItems(result?.items ?? [createNewTodoItem()]);
            setLoading(false);
        } catch {
            setError({ message: `Failed to load data` });
        }
    };

    const saveData = async () => {
        if (!items) { return; }
        setLoading(true);
        try {
            await apiService.saveData({ items });
            if (!isMounted.current) { return; }
            setLoading(false);
        } catch {
            setError({ message: `Failed to load data` });
        }
    };

    const changeItem = (value: TodoItemData) => {
        setItems(s => s.map(x => x.key === value.key ? value : x));
    };
    const addItem = () => {
        setItems(s => [...s, createNewTodoItem()]);
    };
    const deleteItem = (value: TodoItemData) => {
        setItems(s => s.filter(x => x.key !== value.key));
    };

    if (error) {
        return (
            <div style={styles.container}>
                <div style={styles.row}>
                    <span>{`‚ùó ${error.message}`}</span>
                </div>
            </div>
        );
    }

    return (
        <div style={styles.container}>
            {loading && (
                <div style={styles.row}>
                    <span>{`üïí Loading`}</span>
                </div>
            )}
            <div style={styles.row}>
                <div style={styles.flex1} />
                <div style={styles.button} onClick={loadData} >
                    <span>{`üíΩ Reload`}</span>
                </div>
                <div style={styles.button} onClick={saveData} >
                    <span>{`üíæ Save`}</span>
                </div>
            </div>
            {items.map(x => (
                <TodoItem key={x.key} item={x} onChange={changeItem} onDelete={deleteItem} />
            ))}
            <div style={styles.row}>
                <div style={styles.button} onClick={addItem} >
                    <span>{`‚ûï Add`}</span>
                </div>
            </div>
        </div>
    );
};


const TodoItem = (props: { item: TodoItemData, onChange: (value: TodoItemData) => void, onDelete: (value: TodoItemData) => void }) => {

    const [isEditing, setIsEditing] = useState(false);
    const [itemText, setItemText] = useState(props.item.text);
    const changeText = () => {
        setIsEditing(false);
        props.onChange({ ...props.item, text: itemText });
    };
    const toggleDone = () => {
        props.onChange({ ...props.item, isDone: !props.item.isDone });
    };

    return (
        <div style={styles.itemContainer}>
            {!isEditing && (
                <div style={styles.row} onClick={toggleDone}>
                    <div>
                        <span>{props.item.isDone ? `‚úî üê±‚Äçüèç ` : `‚óª üòæ `}</span>
                    </div>
                    <div style={styles.flex1}>
                        <span>{itemText}</span>
                    </div>
                    <div style={styles.button}
                        onClick={(e) => { e.preventDefault(); setIsEditing(true); }}
                    >
                        <span>{`‚úè Edit`}</span>
                    </div>
                    <div style={styles.button}
                        onClick={(e) => { e.preventDefault(); props.onDelete(props.item); }}
                    >
                        <span>{`‚ùå Delete`}</span>
                    </div>
                </div>
            )}
            {isEditing && (
                <div style={styles.row}>
                    <input style={styles.flex1} type='text' value={itemText}
                        autoFocus
                        onChange={e => setItemText(e.target.value)}
                        onBlur={e => { e.preventDefault(); changeText(); }}
                        onKeyDown={e => { if (e.key === `Enter`) { e.preventDefault(); changeText(); } }}
                    />
                    <div style={styles.button} onClick={changeText}>
                        <span>{`‚úî Done`}</span>
                    </div>
                </div>
            )}
        </div>
    );
};

export type TodoData = {
    items: TodoItemData[];
};

export type TodoItemData = {
    key: string;
    text: string;
    isDone: boolean;
};

import { TodoData } from './types';

const delay = (timeMs = 0) => {
    return new Promise<void>((resolve) => {
        setTimeout(resolve, timeMs);
    });
};

const simulateNetwork = async () => {
    await delay(250);
};

/** Create a mock api service that uses localStorage and simulates a network delay */
const createApiService = () => {

    const DATA_KEY = `todo`;

    const service = {
        loadData: async () => {
            await simulateNetwork();
            try {
                const json = localStorage.getItem(DATA_KEY);
                if (!json) { return null; }
                return JSON.parse(json) as TodoData;
            } catch {
                return null;
            }
        },
        saveData: async (data: TodoData) => {
            await simulateNetwork();
            localStorage.setItem(DATA_KEY, JSON.stringify(data));
        },
    };
    return service;
};

export const apiService = createApiService();
