// type Hider = {};
/* eslint-enable no-irregular-whitespace */

/** ### Typescript Type System Adventure
 * 
 * @author Rick Love
 * @date   2020-10-24
 * @license This work is licensed under a Creative Commons Attribution-NonCommercial-NoDerivatives 4.0 International License.
 * 
 * ### Ascii Art
 * 
 * ```text
 *  .......................................................
 *  :                                                     :
 *  : Typescript Type System Adventure                    :
 *  : by: Rick Love                                       :
 *  :                                           _____     :
 *  : _________________________________        |     |    :
 *  : |----|  Typescript        |-----|        |     |    :
 *  : |----|   Type             |-----|        |     |    :
 *  : |----|    System          |-----|        |     |    :
 *  : |----|     Adventure      |-----|        |     |    :
 *  : |----|______by: Rick Love_|-----|        |     |    :
 *  : |-------------------------------|        |     |    :
 *  : |-------------------------------|        |     |    :
 *  : |-------_________________-------|        |     |    :
 *  : |------|            |    |------|      __|     |__  :
 *  : |------|  --        |    |------|       *       *   :
 *  : |------| |  |       |    |------|        *     *    :
 *  : |------| |__|       |    |------|         *   *     :
 *  :  *_____|____________|____|______|          * *      :
 *  :                                             *       :
 *  :.....................................................:
 * ```
 * 
 * ### Summary
 * 
 * So this was written for fun!
 * 
 * ### Note: 
 * 
 * This is a work of fiction. It's not code that you can run or anything. It's a fun toy playing with the typescript type system.
 *
 * Feel free to learn and make your own, but please give me credit for coming up with the idea with this link:
 * https://ricklove.me/typescript-type-system-adventure
 * 
 * ### Inspiration:
 * 
 * Also this was inspired by this amazing SQL typescript type system parser:
 * https://github.com/codemix/ts-sql
 * 
 * ### Sharing
 * 
 * **Please share it with all your nerd friends!**
 * 
 * https://ricklove.me/typescript-type-system-adventure
 */
export type GameStart = Hider & GameStateCommon & { hasGameStarted: false };

type GameStateCommon<TLocation extends string = string, TInventory extends {} = {}, TEnvironment extends {} = {}> = {
    command: <T extends GameStateCommon, TCommand extends string>(this: T, command: TCommand) => Command<T, TCommand>;
    location: TLocation;
    inventory: TInventory;
    environment: TEnvironment;
};
type SetLocation<T extends GameStateCommon, TNew extends string> = { location: TNew } & Omit<T, 'location'>;
type SetInventory<T extends GameStateCommon, TNew> = { inventory: TNew & Omit<T['inventory'], keyof TNew> } & Omit<T, 'inventory'>;
type SetEnvironment<T extends GameStateCommon, TNew> = { environment: TNew & Omit<T['environment'], keyof TNew> } & Omit<T, 'environment'>;

type Location_InFrontOfHouse = 'In Front of House';
type Location_OutsideOldHouse = 'Outside Old House';

export type Command<TState extends GameStateCommon, TCommand> =
    TCommand extends 'inv' | 'inventory' ? {
        /** ### Inventory
         * 
         * You examine your inventory.
         * 
         * How does all this stuff fit in here anyway?
         * 
         * (ðŸ”Ž Look at the inventory type at the top â¬†)
         */
        execute: { inventory: TState['inventory'] } & TState;
    }
    : Hider &

    // Game Start
    [TCommand, TState] extends ['help', {}] ?
    {
        /** ### Help
         * 
         * Commands to try:
         * 
         * - look
         * - inventory
         * - open mailbox
         * - open envelope
         * - read letter
         * - move house
         * 
         */
        execute: TState;
    } :

    // Game Start
    [TCommand, TState] extends ['begin', { hasGameStarted: false }] ?
    {
        /** ### Begin Your Adventure
         * 
         * You have begun an amazing adventure!
         * 
         * Have fun!
         * 
         * Commands to try:
         * 
         * - look
         * - inventory
         * - open ...
         * - read ...
         * - break ...
         * 
         */
        execute: Hider & GameStateCommon<Location_InFrontOfHouse> & { hasGameStarted: true };
    }

    // In Front of House
    : [TCommand, TState] extends ['look', { location: Location_InFrontOfHouse, environment: { mailbox?: 'closed' } }] ?
    {
        /** ### In Front of House
         * 
         * You are in front of an old house.
         * 
         * There is a ***mailbox*** nearby.
         */
        execute: TState;
    }
    : [TCommand, TState] extends ['open mailbox', { location: Location_InFrontOfHouse, environment: { mailbox?: 'closed' } }] ?
    {
        /** ### Open Mailbox 
         * 
         * You open the mainbox and find an envelope inside.
         * 
         * The front of the ***envelope*** reads: 
         * 
         * 'To: The Finder of This Letter'
         */
        execute: Hider & SetInventory<SetEnvironment<TState, { mailbox: 'open' }>, { envelope: true }>;
    }
    : [TCommand, TState] extends ['open mailbox', { location: Location_InFrontOfHouse, environment: { mailbox: 'open' } }] ?
    {
        /** ### Mailbox Already Open
         * 
         * The mailbox is already open.
         * 
         * Really, you just opened it a second ago.
         */
        execute: TState;
    }

    // Envelope
    : [TCommand, TState] extends ['look', { location: Location_InFrontOfHouse, inventory: { envelope: true } }] ?
    {
        /** ### In Front of House
         * 
         * You are standing in front of an old house.
         * 
         * You have an ***envelope*** in your hand.
         */
        execute: TState;
    }
    : [TCommand, TState] extends ['open envelope', { inventory: { envelope: true } }] ?
    {
        /** ### Open Envelope 
         * 
         * You open the envelope and find an single sheet of paper inside.
         * 
         * The paper is very brittle, it must be very old. You've never touched parchment before, but this is probably it. 
         * 
         * You wonder how such an old ***letter*** could have been in this mailbox.
         */
        execute: Hider & SetInventory<TState, { letter: true, envelope: false }>;
    }

    // Letter
    : [TCommand, TState] extends ['look', { location: Location_InFrontOfHouse, inventory: { letter: true } }] ?
    {
        /** ### In Front of House
         * 
         * You are standing in front of an old house with an old ***letter*** in your hand.
         * 
         * You feel strangely drawn to the ***letter***.
         */
        execute: TState;
    }
    : [TCommand, TState] extends ['read letter', { inventory: { letter: true | 'read' } }] ?
    {
        /** ### Read Letter
         * 
         * You read the old ***letter***:
         * 
         * > You are beginning to enter a dangerous realm. 
         * > What you thought you knew previously will soon be a shadow in your dreams.
         * > Find the best way forward and do not listen to the foolish.
         * >
         * > It is not the ***unknown*** which you should fear. 
         * >
         * > Beware the ***any***!
         */
        execute: Hider & SetInventory<TState, { letter: 'read' }>;
    }

    // Approach House
    : [TCommand, TState] extends ['look', { location: Location_InFrontOfHouse, inventory: { letter: 'read' } }] ?
    {
        /** ### In Front of House
         * 
         * You are in front of an old house.
         * 
         * There is an open mailbox nearby.
         * 
         * You notice a light flickering in the house.
         */
        execute: TState;
    }
    : [TCommand, TState] extends ['look house', { location: Location_InFrontOfHouse, inventory: { letter: 'read' } }] ?
    {
        /** ### Look at Old House
         * 
         * Yeah, that is an old house.
         * 
         * Maybe you should ***walk*** up to it.
         */
        execute: TState;
    }
    : [TCommand, TState] extends ['approach house' | 'move house' | 'walk house', { location: Location_InFrontOfHouse, inventory: { letter: 'read' } }] ?
    {
        /** ### Approach House
         * 
         * You walk towards the old house.
         * 
         * You hear the wind rustling through the trees, and a branch scratching against a window in the back of the house.
         * 
         * As you get near, the flickering light buzzes and finally goes out.
         */
        execute: SetLocation<TState, Location_OutsideOldHouse>;
    }

    // House Door
    : [TCommand, TState] extends ['look', { location: Location_OutsideOldHouse }] ?
    {
        /** ### Outside Old House
         * 
         * You are standing outside an old house.
         * 
         * The wind is getting very strong now and you need to find shelter.
         * 
         * In the ***window*** beside the ***door***, you see a sign that says:
         * 
         * "May all those who enter as guests, leave ~~as friends~~ *without giving us Cornavirus*"
         * 
         */
        execute: TState;
    }
    : [TCommand, TState] extends ['break window', { location: Location_OutsideOldHouse }] ?
    {
        /** ### Break the Window
         * 
         * Maybe if you break the window, you will be able to crawl through it to get into the house.
         * 
         * However, you quickly regret your decision...
         * 
         */
        execute: {
            /** ### You Are Dead
             * 
             * Breaking that window may have been a little aggressive.
             * 
             * At least it seems that whatever was inside the house thought so.
             * 
             * You don't know exactly what happened, but you are dead.
             * 
             * ![](https://ricklove.me/blog-content/posts/2020-10-24-typescript-type-system-adventure/broken-window-eye.gif)
             */
            youAreDead: GameOver;
        };
    }
    : [TCommand, TState] extends ['open door', { location: Location_OutsideOldHouse }] ?
    {
        /** ### Open the Door
         * 
         * Just make yourself at home! 
         * 
         * Or maybe not...
         */
        execute: {
            /** ### You Are Dead
             * 
             * The owners didn't appreciate your rude entrance and neither did their shotgun.
             * 
             * You are dead.
             * 
             */
            youAreDead: GameOver;
        };
    }
    : [TCommand, TState] extends ['ring doorbell', { location: Location_OutsideOldHouse }] ?
    {
        /** ### Ring the Doorbell
         * 
         * You press the button for the doorbell, but don't hear anything.
         * 
         * After waiting a moment, nothing seems to have happened.
         * 
         */
        execute: TState;
    }
    : [TCommand, TState] extends ['knock door', { location: Location_OutsideOldHouse }] ?
    {
        /** ### Knock on the Door
         * 
         * You knock on the door.
         * 
         * It appears someone is home, you hear footsteps inside...
         */
        execute: {
            /** * ### Wait at Door
             * 
             * After waiting a moment, a nice old lady appears:
             * 
             * > Welcome!
             * >
             * > I always like it when children visit.
             * > 
             * > I just got done baking some chocolate chip cookies.
             */
            wait: {
                /** ### Eat the Cookie
                 * 
                 * You take a cookie from the plate the lady is pushing towards you.
                 * 
                 * You aren't sure about it, but decide that it's better not to be rude.
                 */
                eatCookie: {
                    /** ### You are Now Being Tracked
                     * 
                     * You accepted the cookies.
                     * 
                     * Now everywhere you go you see ads about the last thing that you mentioned to your friend in private conversation.
                     * 
                     * Oh well, worse things could have happened.
                     * 
                     */
                    youAreNowBeingTracked: GameWon;
                };
            };
        };
    }

    // The Unknown
    : [TCommand, TState] extends ['unknown', {}] ? {
        /** ### `unknown` is Safe
         * 
         * You have begun to wonder into an unknown type space.
         * 
         * The good news is that you didn't succumb to the 'any', so you are still safe.
         * 
         */
        execute: TState;
    }
    // The Any
    : [TCommand, TState] extends ['any', {}] ? {
        /** ### `any` is Evil
         * 
         * You have entered an endless space devoid of meaning.
         * 
         * GAME OVER!
         * 
         * Beware the `any`!
         * 
         * To learn how to avoid `any`, read more on my blog:
         * 
         * https://ricklove.me/typescript-any-is-evil
         * 
         * */
        // eslint-disable-next-line @typescript-eslint/no-explicit-any
        execute: any;
    }
    // Unknown
    : 'UNKNOWN COMMAND - Try Again' & {
        /** ### Unknown Command
         * 
         * I don't know what you mean. Try Again.
         * 
         */
        execute: TState;
    };

type GameOver = {
    /** ### GAME OVER!
     * 
     * You are dead!
     * 
     * You regret your decisions. Try again.
     * 
     * https://ricklove.me/typescript-type-system-adventure
     * 
     * ```text
     * MMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMM
     * MMMMMMMMMMMM        MMMMMMMMMMMM
     * MMMMMMMMMM            MMMMMMMMMM
     * MMMMMMMMM              MMMMMMMMM
     * MMMMMMMM                MMMMMMMM
     * MMMMMMM                 MMMMMMMM
     * MMMMMMM                  MMMMMMM
     * MMMMMMM                  MMMMMMM
     * MMMMMMM    MMM    MMM    MMMMMMM
     * MMMMMMM   MMMMM   MMMM   MMMMMMM
     * MMMMMMM   MMMMM   MMMM   MMMMMMM
     * MMMMMMMM   MMMM M MMMM  MMMMMMMM
     * MMVKMMMM        M        MMMMMMM
     * MMMMMMMM       MMM      MMMMMMMM
     * MMMMMMMMMMMM   MMM  MMMMMMMMMMMM
     * MMMMMMMMMM MM       M  MMMMMMMMM
     * MMMMMMMMMM  M M M M M MMMMMMMMMM
     * MMMM  MMMMM MMMMMMMMM MMMMM   MM
     * MMM    MMMM M MMMMM M MMMM    MM
     * MMM    MMMM   M M M  MMMMM   MMM
     * MMMM    MMMM         MMM      MM
     * MMM       MMMM     MMMM       MM
     * MMM         MMMMMMMM      M  MMM
     * MMMM  MMM      MMM      MMMMMMMM
     * MMMMMMMMMMM  MM       MMMMMMM  M
     * MMM  MMMMMMM       MMMMMMMMM   M
     * MM    MMM        MM            M
     * MM            MMMM            MM
     * MMM        MMMMMMMMMMMMM       M
     * MM      MMMMMMMMMMMMMMMMMMM    M
     * MMM   MMMMMMMMMMMMMMMMMMMMMM   M
     * MMMMMMMMMMMMMMMMMMMMMMMMMMMMMMMM
     * ```
     * Ascii Art From: https://asciiart.website/index.php?art=people/skeletons
     * 
     * */
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    gameOver: GameOver;
}

type GameWon = {

    /** ### Winner!
     * 
     * You have won! 
     * 
     * Great job!
     * I hope this was fun.
     * 
     * **Please share it with all your nerd friends!**
     * 
     * https://ricklove.me/typescript-type-system-adventure
     * 
     * */
    winner: GameWon;
};

// Play Game
type Step00 = GameStart;
type Step01 = Command<Step00, 'begin'>['execute'];
type Step02 = Command<Step01, 'look'>['execute'];
type Step03 = Command<Step02, 'open mailbox'>['execute'];
type Step04 = Command<Step03, 'inventory'>['execute'];
type Step04b = Command<Step04, 'look'>['execute'];
type Step05 = Command<Step04b, 'open envelope'>['execute'];
type Step06 = Command<Step05, 'inventory'>['execute'];
type Step07 = Command<Step06, 'read letter'>['execute'];
type Step07b = Command<Step07, 'look'>['execute'];
type Step07c = Command<Step07, 'unknown'>['execute'];
type Step07d = Command<Step07, 'any'>['execute'];
type Step08 = Command<Step07b, 'walk house'>['execute'];
type Step08b = Command<Step08, 'look'>['execute'];

const play = () => {
    const gameStart = null as unknown as GameStart;
    const result = gameStart
        .command(`begin`).execute
        .command(`look`).execute
        .command(`open mailbox`).execute
        .command(`look`).execute
        .command(`inv`).execute
        .command(`open envelope`).execute
        .command(`read letter`).execute
        .command(`open mailbox`).execute
        .command(`look`).execute
        .command(`look house`).execute
        .command(`walk house`).execute
        .command(`look`).execute
        // .command(`break window`).execute.youAreDead.gameOver
        // .command(`open door`).execute.youAreDead.gameOver
        .command(`ring doorbell`).execute
        .command(`knock door`).execute.wait.eatCookie.youAreNowBeingTracked.winner.winner
        ;
};

