export const gameStart = {
  __: hider,
  /** ### Begin Your Adventure
   *
   * You have begun an amazing adventure!
   *
   * Have fun!
   */
  begin: () => inFrontOfHouse,
};

const gameWon = {
  __: hider,

  /** ### Winner!
   *
   * You have won!
   *
   * Great job!
   * I hope this was fun.
   *
   * **Please share it with all your nerd friends!**
   *
   * https://ricklove.me/typescript-type-system-adventure
   *
   * */
  end: () => {},
};

const gameOver = {
  __: hider,

  /** ### Game Over
   *
   * You have entered an endless space devoid of meaning.
   *
   * You have failed!
   *
   * Beware the any!
   *
   * https://ricklove.me/typescript-any-is-evil
   *
   * */
  end: () => {},
};

/**
 * ### Objects
 *
 * - Objects contain:
 *   - location: string = Location Name
 *   - functions = actions
 *   - fields = inventory
 *   - gameOver = Game is over, no more actions
 *
 * ### { location: string }
 *
 * - Set the location name
 * - This resets the action scope
 *   - All actions are removed from scope
 *   - Inventory items stay in scope
 * - Locations can never be entered again
 *   - no need to keep state of old location scope
 *   - returing to the same location is the same as going to a new location
 *
 * ### { function: () => {} }
 *
 * - represent actions
 * - it's jsdoc will be displayed upon activation
 * - it is removed from scope once done (it should use `return this` to not change game state)
 *
 * ### { field: {} }
 *
 * - represents an inventory item
 * - it's jsdoc is it's inventory description
 * - it is added to the scope at first occurance
 * - it will stay in scope when accessed
 * - a duplicate entry will expand it's scope
 * - it is removed from scope only when set to null|undefined
 *   - e.g. `{ envelope: undefined }`
 * - an empty object will allow changing the jsdoc for a used inventory item
 *   - e.g. `{ letter: {} }`
 *
 * ### { gameOver }
 *
 * - The game is over
 * - it's jsdoc is the failed conclusion
 * - it will followup with a standard game over / continue message
 *
 * * ### { gameWon }
 *
 * - The win state
 * - it's jsdoc is the final part of the story
 * - it will followup with a game over message
 *
 */
type Rules = {};

const inFrontOfHouse = {
  location: `In Front of House` as const,
  __: hider,

  /** ### In Front of House
   *
   * You are in front of an old house.
   *
   * There is a closed ***mailbox*** nearby.
   */
  look<T>(this: T) {
    return this;
  },

  /** ### Open Mailbox
   *
   * You open the mailbox and find an ***envelope*** inside.
   *
   * The front of the ***envelope*** reads:
   *
   * 'To: The Finder of This Letter'
   */
  openMailbox: () => ({
    __: hider,

    /** ### In Front of House
     *
     * You are standing in front of an old house.
     *
     * You have an ***envelope*** in your hand.
     */
    look<T>(this: T) {
      return this;
    },

    /** Old Envelope
     *
     * An old envelope.
     *
     * You feel compelled to ***open*** it.
     */
    envelope: {
      /** ### Open Envelope
       *
       * You open the envelope and find a ***letter*** written on a single sheet of paper.
       *
       * The paper is very brittle, it must be very old. In fact, you would even call this parchment.
       */
      openEnvelope: () => ({
        __: hider,

        envelope: undefined,

        /** ### In Front of House
         *
         * You are standing in front of an old house with an old letter in your hand.
         *
         * You feel strangely drawn to the letter.
         */
        look<T>(this: T) {
          return this;
        },

        /** Old Letter
         *
         * An old letter written on parchment.
         *
         * You feel compelled to ***read*** it.
         */
        letter: {
          /** ### Read Letter
           *
           * You read the old letter:
           *
           * > You are beginning to enter a dangerous realm.
           * > What you thought you knew previously will soon be a shadow in your dreams.
           * > Find the best way forward and do not listen to the foolish.
           * >
           * > It is not the unknown which you should fear.
           * >
           * > Beware the any!
           */
          readLetter: () => ({
            __: hider,

            /** ### Old Letter
             *
             * You read the old letter:
             *
             * > You are beginning to enter a dangerous realm.
             * > What you thought you knew previously will soon be a shadow in your dreams.
             * > Find the best way forward and do not listen to the foolish.
             * >
             * > It is not the unknown which you should fear.
             * >
             * > Beware the any!
             */
            letter: {},

            /** ### In Front of House
             *
             * You are in front of an old house.
             *
             * There is an open mailbox nearby.
             *
             * You notice a light flickering in the house.
             *
             * You feel a powerful curiosity drawing you to ***approach*** the house.
             */
            look<T>(this: T) {
              return this;
            },

            /** ### Approach House
             *
             * You walk towards the old house.
             *
             * You hear the wind rustling through the trees, and a branch scratching against a window in the back of the house.
             *
             * As you get near, the flickering light buzzes and finally goes out.
             */
            approachHouse: () => outsideOldHouse,
          }),
        },
      }),
    },
  }),
};

const outsideOldHouse = {
  location: `Outside Old House` as const,
  __: hider,

  /** ### Outside Old House
   *
   * You are standing outside an old house.
   *
   * The wind is getting very strong now and you need to find shelter.
   *
   * In the ***window*** beside the ***door***, you see a sign that says:
   *
   * "May all those who enter as guests, leave ~~as friends~~ *without giving us Cornavirus*"
   *
   * There is also a ***doorbell*** near the door.
   */
  look<T>(this: T) {
    return this;
  },

  /** Window
   *
   * In the ***window*** next to the ***door***, you see a sign that says:
   *
   * "May all those who enter as guests, leave ~~as friends~~ *without giving us Cornavirus*"
   *
   * The ***window*** is only a single pane and you could easily ***break*** it.
   */
  lookWindow<T>(this: T) {
    return this;
  },

  /** Door
   *
   * The old white wooden door needs a new coat of paint.
   *
   * You could try to ***open*** the ***door*** to see if it is unlocked.
   */
  lookDoor<T>(this: T) {
    return this;
  },

  /** Doorbell
   *
   * There is an doorbell near the door handle.
   *
   * You could try to ***ring*** the ***doorbell***.
   */
  lookDoorbell<T>(this: T) {
    return this;
  },

  /** ### Break the Window
   *
   * Maybe if you break the window, you will be able to crawl through it to get into the house.
   *
   * However, you quickly regret your decision...
   *
   */
  breakWindow: () => ({
    __: hider,

    /** ### You Are Dead
     *
     * Breaking that window may have been a little aggressive.
     *
     * At least it seems that whatever was inside the house thought so.
     *
     * You don't know exactly what happened, but you are dead.
     *
     * ![](https://ricklove.me/blog-content/posts/2020-10-24-typescript-type-system-adventure/broken-window-eye.gif)
     */
    gameOver,
  }),

  /** ### Open the Door
   *
   * Just make yourself at home!
   *
   * Or maybe not...
   */
  openDoor: () => ({
    __: hider,

    /** ### You Are Dead
     *
     * The owners didn't appreciate your rude entrance and neither did their shotgun.
     *
     * You are dead.
     *
     */
    gameOver,
  }),

  /** ### Ring the Doorbell
   *
   * You press the button for the doorbell, but don't hear anything.
   *
   * After waiting a moment, nothing seems to have happened.
   *
   * Maybe you should ***knock*** on the ***door*** instead.
   */
  ringDoorBell: () => outsideOldHouse,

  /** ### Knock on the Door
   *
   * You knock on the door.
   *
   * It appears someone is home, you hear footsteps inside...
   *
   * You're getting worried: should you ***wait*** or ***run***?
   */
  knockOnDoor: () => ({
    __: hider,

    /** * ### Wait at Door
     *
     * After waiting a moment, a nice old lady appears:
     *
     * > Welcome!
     * >
     * > I always like it when children visit.
     * >
     * > I just got done baking some chocolate chip cookies.
     * >
     * > Come inside.
     *
     * The old lady seems nice enough. It is probably safe to ***go inside*** with here.
     */
    wait: () => ({
      __: hider,

      /** Go Inside the Old House
       *
       * The old lady seems nice enough.
       *
       * You follow her to the livingroom.
       *
       * > Please! ***Sit Down***.
       * >
       * > I'll be right back with some fresh cookies.
       */
      goInside: () => insideHouse,
    }),
  }),
};

const insideHouse = {
  location: `Inside the Old House` as const,

  /** ### Inside Old House
   *
   * You are standing in the living room of the old house.
   *
   * You hear the old lady in the kitchen moving things around. She said she was going to bring some cookies.
   *
   * You can smell something like burnt cookies, but you also smell mildew and dust.
   *
   * There is an antique couch nearby. It doesn't look to comfortable, but it would be polite to ***sit down***.
   */
  look<T>(this: T) {
    return this;
  },

  /** Sit Down
   *
   * You sit on the antique couch.
   *
   * It's pretty stiff: Like someone stappled a few cottonballs to a board and covered it with some cloth.
   *
   * The old lady finally enters the room holding a plate of cookies.
   *
   * She walks in front of you and holds out the plate:
   *
   * > Here you go, Sugar!
   * >
   * > Grandma always likes to spoil here grandkids.
   *
   * You look at the cookies. They don't look too bad, and you don't think there is anything you can do but ***accept*** a ***cookie***.
   */
  sitDown: () => ({
    /** ### Inside Old House
     *
     * You are sitting on an antique couch.
     *
     * The old lady is standing in front of you waiting for you to ***accept*** a ***cookie***.
     *
     */
    look<T>(this: T) {
      return this;
    },

    /** Accept Cookie
     *
     * You accept a cookie.
     *
     * It looks like it is an oatmeal chocolate chip cookie with raisins.
     *
     * It's only slightly burnt. Otherwise, it doesn't look too bad.
     */
    acceptCookie: () => ({
      /** ### Inside Old House
       *
       * You are sitting on an antique couch.
       *
       * The old lady is standing in front of you waiting for you to ***eat*** the ***cookie*** that is in your hand.
       *
       */
      look<T>(this: T) {
        return this;
      },

      /** Cookie
       *
       * It looks like it is an oatmeal chocolate chip cookie with raisins.
       *
       * It's only slightly burnt. Otherwise, it doesn't look too bad.
       *
       * You better *eat* it or the lady might be offended.
       */
      cookie: {
        /** ### Eat the Cookie
         *
         * You eat the cookie.
         *
         * It tastes ok, but it has a strange aftertaste that never goes away.
         */
        eatCookie: () => ({
          __: hider,

          /** ### You are Now Being Tracked
           *
           * You accepted cookies.
           *
           * Now everywhere you go you see ads about the last thing you mentioned to your friend in private conversation.
           *
           * Oh well, worse things could have happened.
           *
           */
          gameWon,
        }),
      },
    }),
  }),
};

// Play Game (Test)
const play = () => {
  gameStart
    .begin()
    .look()
    .openMailbox()
    .envelope.openEnvelope()
    .look()
    .letter.readLetter()
    // .letter
    .look()
    .approachHouse()
    .look()
    .lookWindow()
    .lookDoor()
    .lookDoorbell()
    .ringDoorBell()
    // .breakWindow().gameOver.end
    // .openDoor().gameOver.end
    .knockOnDoor()
    .wait()
    .goInside()
    .look()
    .sitDown()
    .acceptCookie()
    .cookie.eatCookie().gameWon.end;
};
