pragma solidity ^0.8.0;

import "./utils/SafeMath.sol";

contract CanaryEnigma {

    using SafeMath for uint256;

    uint256 public immutable POT_SIZE;
    uint256 public immutable DURATION_IN_DAYS;
    uint256 public immutable REWARD_PER_DAY;
    uint256 public immutable START_TIMESTAMP;
    bytes32[] public ANSWER_HASHES;

    uint256 public currentRiddle = 0;
    uint256 public potCashedOut = 0;
    uint256 public lastAnswerTimestamp;

    constructor(
        uint256 potSize,
        uint256 rewardPerDay,
        uint256 durationInDays,
        bytes32[] memory answerHashes
    ) payable {
        require(potSize == msg.value, "Ether value sent is incorrect");
        require(potSize  == rewardPerDay.mul(durationInDays), "Wrong Pot size");

        POT_SIZE = potSize;
        DURATION_IN_DAYS = durationInDays;
        REWARD_PER_DAY = rewardPerDay;
        START_TIMESTAMP = block.timestamp;
        lastAnswerTimestamp = block.timestamp;
        ANSWER_HASHES = answerHashes;
    }

    function decipher(string memory answer) public {
        require(runningPot() > 0, "Enigma no longer active");
        require(block.timestamp.sub(START_TIMESTAMP) / 60 / 60 / 24 < DURATION_IN_DAYS, "Enigma no longer active");
        require(verifyAnswer(answer), "Wrong answer");

        uint256 pot = runningPot();
        currentRiddle++;
        lastAnswerTimestamp = block.timestamp;
        potCashedOut += pot;
        payable(msg.sender).transfer(pot);
    }

    function runningPot() public view returns (uint256) {
        uint256 elapsedDays = daysElapsedSinceLastAnswer();
        uint256 pot = REWARD_PER_DAY.mul(elapsedDays.add(1));

        return pot < POT_SIZE.sub(potCashedOut) ? pot : POT_SIZE.sub(potCashedOut); // Getting the min value of both
    }

    function daysElapsedSinceLastAnswer() public view returns (uint256) {
        return block.timestamp.sub(lastAnswerTimestamp) / 60 / 60 / 24;
    }

    function verifyAnswer(string memory answer) public view returns (bool) {
        require(currentRiddle < ANSWER_HASHES.length, "Enigma no longer active");

        return keccak256(abi.encodePacked(answer)) == ANSWER_HASHES[currentRiddle];
    }
}
